#!/usr/bin/env python3
from cv2 import VideoCapture, imencode, getNumberOfCPUs, imread
from threading import Thread
from collections import deque
from time import sleep


def DummyVideoPort():
    return 0


def checkVideoPort():
    from subprocess import Popen, PIPE
    from os import getcwd
    from os.path import isdir, isfile

    if isdir(getcwd() + "/app/hardware/bashScripts"):
        scriptDirPath = getcwd() + "/app/hardware/bashScripts/"
    else:
        scriptDirPath = "/home/orangepi/mitziProject/app/hardware/bashscripts/"
    camerascript = scriptDirPath + "camerachecker.sh"
    if not isfile(camerascript):
        print("ERROR FINDING CAMERA SCRIPT")
        print(camerascript)
        exit()

    p = Popen(["/bin/bash", camerascript], stdout=PIPE)
    index = p.stdout.read()
    if index:
        try:
            indx = int(index)
            print("Found camera port: ", indx)
            return indx
        except:
            pass
    print("Couldn't find camera port, using default value 0")
    return 0


def dummyImgPath(path=None):
    if path is None:
        from os import getcwd
        from os.path import isdir
        if isdir(getcwd() + "/app/hardware/static"):
            staticDirPath = getcwd() + "/app/hardware/static/"
        else:
            staticDirPath = "/home/orangepi/motorProject/app/hardware/static/"
        ImgPath = staticDirPath + "dummyImg.jpg"
    else:
        ImgPath = path

    from os.path import isfile
    if not isfile(ImgPath):
        print("ERROR FINDING MOTOR ONE SCRIPT")
        print(ImgPath)
        exit()

    return ImgPath


class DummyVideoCapture:
    def __init__(self, portID=0):
        self.id = portID
        self.open = True
        self.imgPath = dummyImgPath()

    def isOpened(self):
        return self.open

    def release(self):
        self.open = False

    def read(self):
        return True, imread(self.imgPath)


class DummyVideoCamera:

    def __init__(self, portID=None):
        if portID is None:
            self.port = checkVideoPort()
        else:
            self.port = portID
        self.threadNum = getNumberOfCPUs()
        self.pending = deque()
        self.camera = DummyVideoCapture(self.port)
        self.t = Thread(target=self.update, args=(), daemon=True)

        if not self.camera.isOpened():
            print("Couldnt open camera")
            self.status, self.img = False, [0, 0, 0]
            self.running = False
            self.initSuccess = False
        else:
            self.running = True
            self.initSuccess = True
            self.status, self.img = self.camera.read()
            print("Opening Camera")

        self.t.start()

    def isRunning(self):
        return self.running

    def isInit(self):
        return self.initSuccess

    def isOK(self):
        if self.running and self.initSuccess:
            return 1
        return 0

    def startThread(self):
        if self.initSuccess:
            if self.running:
                self.terminateThread()
            self.running = True
            self.t = Thread(target=self.update, args=(), daemon=True)
            self.t.start()

    def terminateThread(self):
        self.running = False
        try:
            self.t.join()
        except RuntimeError:
            pass

    def terminateCamera(self):
        self.terminateThread()
        if self.initSuccess:
            self.camera.release()
            self.initSuccess = False

    def changePort(self, portID=None):
        if portID is not None:
            self.port = portID
        self.terminateCamera()
        self.camera = DummyVideoCapture(self.port)
        if not self.camera.isOpened():
            print("Couldn't open camera")
        else:
            self.initSuccess = True
            print("Opening Camera")

    def reStart(self, portID=None):
        self.changePort(portID=portID)
        self.startThread()

    def __del__(self):
        self.terminateCamera()

    def update(self):
        print("Camera thread is running")
        fail_counter = 0
        if self.initSuccess:
            while self.running:
                if len(self.pending) < self.threadNum:
                    success, new_img = self.camera.read()
                    if success:
                        if len(self.pending) > 0:
                            self.img = new_img
                        _, jpeg = imencode('.jpeg', new_img)
                        self.pending.append(jpeg.tobytes())
                    elif fail_counter > 3:
                        self.running = False
                    else:
                        fail_counter += 1
                sleep(0.001)
        print("Camera thread stopped")

    def get_port(self):
        return self.port

    def get_img(self):
        return self.img

    def get_jpeg_frame(self):
        if len(self.pending) > 0:
            return self.pending.popleft()
        else:
            _, jpeg = imencode('.jpeg', self.img.copy())
            return jpeg.tobytes()


