def getCameraGenerator(cam=None):
    noSignalFrame =  b'--frame\r\n' b'Content-Type: image/jpeg\r\n\r\n' + getNoSignalFrame() + b'\r\n\r\n'
    if type(cam) is VideoCameraCV:
        while True:
            if cam.isOK():
                frame = cam.get_jpeg_frame()
                yield b'--frame\r\n' b'Content-Type: image/jpeg\r\n\r\n' + frame + b'\r\n\r\n'
            else:
                yield noSignalFrame

    elif type(cam) is DummyVideoCamera:
        frame = cam.get_jpeg_frame()
        while True:
            if cam.isOK():
                yield b'--frame\r\n' b'Content-Type: image/jpeg\r\n\r\n' + frame + b'\r\n\r\n'
            else:
                yield noSignalFrame
    else:
        print("Cam: ", type(cam))
        while True:
            yield noSignalFrame


def getCameraFrame(cam=None):
    if type(cam) is VideoCameraCV:
        if cam.isOK():
            frame = cam.get_jpeg_frame()
            return b'--frame\r\n' b'Content-Type: image/jpeg\r\n\r\n' + frame + b'\r\n\r\n'
    elif type(cam) is DummyVideoCamera:
        if cam.isOK():
            frame = cam.get_jpeg_frame()
            return b'--frame\r\n' b'Content-Type: image/jpeg\r\n\r\n' + frame + b'\r\n\r\n'
    else:
        print("Cam: ", type(cam))
    frame = getNoSignalFrame()
    return b'--frame\r\n' b'Content-Type: image/jpeg\r\n\r\n' + frame + b'\r\n\r\n'


